SELECT TO_STRING(QUANTIZE(TO_TIME(TO_TIMESTAMP(date, time)), 3600),'HH') AS [Time of the day], Avg(time-taken) As [Average time-taken], Count(*) As [Hits], MAX(time-taken) As [Max time-taken], MIN(time-taken) As [Min time-taken]  INTO '%outputPath%' FROM  '%logPath%' WHERE cs-uri-stem = '%url%' GROUP BY [Time of the day] ORDER BY [Time of the day]