SELECT TOP 15 REPLACE_CHR(cs-uri-stem, '+', ' ') As [URL], COUNT(*) As [Hits], SUM(time-taken) As [Cost], Avg(time-taken) As [Average execution time], MAX(time-taken) As [Max execution time], MIN(time-taken) As [Min execution time]  INTO '%outputPath%' FROM  '%logPath%' GROUP BY [URL] ORDER By [Hits] DESC